/**
 * Coerce a value into a boolean. Undefined/null/unknown values become false.
 */
export declare function toBoolean(v: any): boolean;
/**
 * Coerce a value into a boolean or null. Undefined/null/unknown values become null.
 */
export declare function toNullableBoolean(v: any): boolean | null;
/** Normalize a key: trim and lowercase. */
export declare function normalizeKey(key: string): string;
/** Check if a (normalized) key exists in the provided registry. */
export declare function hasNormalizedKey<K extends string, V>(key: string, registry: Readonly<Record<K, V>>, normalizer?: (k: string) => string): key is K;
/** Get a value from a registry by normalized key. */
export declare function getFromRegistry<K extends string, V>(key: string, registry: Readonly<Record<K, V>>, normalizer?: (k: string) => string): V | undefined;
/** Map over a registry into an array using key and value. */
export declare function mapRegistry<K extends string, V, T>(registry: Readonly<Record<K, V>>, mapper: (key: K, value: V) => T): ReadonlyArray<T>;
