export declare class FriendCode {
    /** Unique friend code ID */
    Id: number;
    /** Friend code string */
    Fc: string;
    /** Friend code type */
    Type: FriendCodeType;
    /** Player ID */
    PlayerId: number;
    /** Whether the code is verified */
    IsVerified: boolean;
    /** Whether the code is primary */
    IsPrimary: boolean;
    /** Creation date (epoch seconds) */
    CreationDate: number;
    /** Optional description */
    Description: string | null;
    /** Whether the code is active */
    IsActive: boolean;
    constructor(data: string | object);
}
export declare enum FriendCodeType {
    Switch = "switch",
    NNID = "nnid",
    ThreeDS = "3ds",
    MKW = "mkw",
    MKT = "mkt"
}
